import { Component } from '@angular/core';

@Component({
  selector: 'app-test',
  template: `
    <div>
      <h *ngIf="name">Welcome {{name}}</h>
    </div>
  `,
  styles: []
})
export class TestComponent {
  name = true;

  onClick(inp: any){
    console.log(inp);
  }
  onInputChange(event : Event){
    const inputValue = (event.target as HTMLInputElement).value
    console.log(inputValue);
  }



}
