import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-observable',
  template: `
    <p>
      observable works!
    </p>
  `,
  styles: [
  ]
})
export class ObservableComponent implements OnInit{

  public const myObservable;

  // Subscribe to the Observable
  ngOnInit(): void {
    this.myObservable = new Observable<number>((observer) => {
      // Emit values to the observer
      observer.next(1); // Emit the first value
      observer.next(2); // Emit the second value
      observer.next(3); // Emit the third value

      // Complete the Observable (no more values will be emitted)
      observer.complete();

      // You can also handle errors if needed
      // observer.error('Something went wrong!');
    });
      this.myObservable.subscribe(
      (value: any) => console.log('Received value:', value),
      (error : any) => console.error('Error:', error),
      () => console.log('Observable complete!')
    );
  }

}
