import { Component } from '@angular/core';

@Component({
  selector: 'app-test',
  template: `
    <div>
      <h1>Welcome {{name}}</h1>
      <input [id]="id" class=""type="text" value="{{name}}" bind-disabled="isDisabled">
      <h2 [class]="textStyle">Angular</h2>
    </div>
  `,
  styles: [`
    .text-success{
      color : green;
    }
    .text-danger{
      color : red;
    }
    .text-special{
      font-style : Italic;
    }
  `]
})
export class TestComponent {
  id = "myInput";
  isDisabled = true;
  name : any = 'javith';
  hasError = true;
  isSpecial = true;



  greet(){
    return "hi"+ this.name;
  }


}
