import { Component } from '@angular/core';

@Component({
  selector: 'app-test',
  template: `
    <div>
      <h1>Welcome {{name}}</h1>
      <input #inp type="text">
      <button (click)="onClick(inp)">Click</button>
    </div>
  `,
  styles: []
})
export class TestComponent {
  name = "Training";

  onClick(inp: any){
    console.log(inp);
  }
  onInputChange(event : Event){
    const inputValue = (event.target as HTMLInputElement).value
    console.log(inputValue);
  }



}
