import { Component } from '@angular/core';

@Component({
  selector: 'app-test',
  template: `
    <div>
      <h1>Welcome {{name}}</h1>
      <input type="text" (input)="onInputChange($event)">
    </div>
  `,
  styles: []
})
export class TestComponent {
  id = "myInput";
  isDisabled = true;
  name : any = 'javith';
  hasError = false;
  isSpecial = true;
  public message = {
    'text-success' : !this.hasError,
    'text-danger' : this.hasError,
    'text-special' : this.isSpecial
  }



  greet(){
    return "hi"+ this.name;
  }


}
