import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-teachers-detail',
  template: `
    <p>
      From snapshot:
      TeacherID.{{teacherId}}
    </p>
    <p>
      From params Observable:
      TeacherID.{{teacherId1}}
    </p>
    <a (click)="goPrevious()">Previous</a>
    <a (click) ="goNext()">Next</a>
  `,
  styles: [
  ]
})
export class TeachersDetailComponent implements OnInit{

  public teacherId : any;
  public teacherId1 : any = '';
  constructor(private route : ActivatedRoute, private router : Router){

  }

  ngOnInit(): void {
    let id = this.route.snapshot.paramMap.get('id');
    let id1 = this.route.params.subscribe((params)=> this.teacherId1 = params['id'])
    this.teacherId = id;

  }

  goPrevious(){
    let previousID = this.teacherId - 1;
    this.router.navigate(['/teachers',previousID]);
  }

  goNext(){
    let nextID = this.teacherId + 1;
    this.router.navigate(['/teachers',nextID]);
  }
}
